(function ($) {
    $.fn.verticalSlider = function (options) {
        var slider = this,
            image,
            slides,
            first,
            active,
            isPaused = false;

        this.init = function () {
            options = $.extend({
                onFirst: function () {
                },
                onLast: function () {
                },
                onSlide: function () {
                }
            }, options);

            image = slider.find('.image');
            slides = slider.find('.slides').children();
            first = slides.first();

            slider.addClass('vertical-slider');

            first.addClass('active');

            image.html(first.clone());

            slider.find('.next').click(slider.next);
            slider.find('.prev').click(slider.prev);

            slider.bind('mousewheel DOMMouseScroll', slider.onMouseWheel);
        };

        this.next = function (e) {
            slider.slide(e, 'next');
        };

        this.prev = function (e) {
            slider.slide(e, 'prev');
        };

        this.slide = function (e, action) {
            e && e.preventDefault();
            if (isPaused) return false;

            active = slider.find('.slides > .active');

            var slide;
            if (action === 'prev')
                slide = active.prev();
            else
                slide = active.next();

            options.onSlide(active, action);

            if (slide.is(slides.last()))
                options.onLast();
            else if (slide.is(slides.first()))
                options.onFirst();

            if (slide.length) {
                var top = (action === 'prev') ? '150' : '-150';
                isPaused = true;
                image.find('img').animate({ top: top + '%' }, 500, function () {
                    slides.removeClass('active');
                    slide.addClass('active');
                    image.html(slide.clone().css('top', top * -1 + '%').animate({ top: '50%' }, 800, slider.resume));
                });
            }
        };

        this.onMouseWheel = function (e) {
            if (e.originalEvent.wheelDelta > 0 || e.originalEvent.detail < 0)
                slider.prev();
            else
                slider.next();
        };

        this.resume = function () {
            isPaused = false;
        };

        this.init();
    };
}(jQuery));


